package ConditionHandelling;

public class Class2 {

	public void Compare( int a) {
		// TODO Auto-generated method stub
      if(a<0 ||a>100){
    	  
    	  System.out.println("Invalid");
      }
      else if (a>=0 && a<30){
    	  System.out.println("Fail");
      }
      else if(a>=31 && a<60)
      {
    	  System.out.println("Pass");
      }
		
      else{
    	  System.out.println("Pass with honors");
      }	
		
		
		
		
		
		
		
		
		
		
	}
	
	
	
	
	
	
	
	
	
	
	
	
	
	
}
