package LoopingConcepts;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int a=0;
		
		for (int i =1;i<=5;i++)
		{

            
			for (int j=1;j<=i;j++)
			{
				System.out.print("*");
			}
			
		
			System.out.println();
		}
		
		
		
		
		
	}

}
