package LoopingConcepts;

public class Test1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for (int i =1;i<=5;i++)
		{
			for (int x=5;x>=i;x--)
			{
				System.out.print(" ");
			}
			
			
			for (int j=1;j<=i;j++)
			{
				System.out.print(" "+"*");
				
			}
			System.out.println("");		
		}
		   
		
		
		
		
		
		
		
		
	}

}
