package LoopingConcepts;

public class Traingle1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int z =1;
		for (int i =1;i<=5;i++)
		{
			
			
			
			for (int j=5;j>i;j--)
			{
				System.out.print(" ");
			}
			for(int j=1;j<=i;j++)
			{
				System.out.print(z+" ");
			z=z+1;
			}
			
			
			
			
			System.out.println();
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
