package Strings;

public class ReverseString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        String rev="";
		String s ="ankit";
		int len = s.length();
		for (int i=len-1;i>=0;i--)
		{
			rev=rev+s.charAt(i);
	
		}
			System.out.print(rev);
		
			
		
		
		
		
		
		
		
		
		
		
		
		
	}

	
}
